import React from 'react';
import { ListGroup, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const dummySubscriptions = [
  { id: 'alice', name: 'Alice' },
  { id: 'bob', name: 'Bob' }
];

function SubscriptionsPage() {
  const handleUnsubscribe = (authorId) => {
    console.log(`Unsubscribed from ${authorId}`);
    // TODO: Call backend to unsubscribe
  };

  return (
    <div>
      <h3>Subscribed Authors</h3>
      <ListGroup>
        {dummySubscriptions.map((author) => (
          <ListGroup.Item key={author.id} className="d-flex justify-content-between align-items-center">
            <Link to={`/author/${author.id}`}>{author.name}</Link>
            <Button
              variant="danger"
              size="sm"
              onClick={() => handleUnsubscribe(author.id)}
            >
              Unsubscribe
            </Button>
          </ListGroup.Item>
        ))}
      </ListGroup>
    </div>
  );
}

export default SubscriptionsPage;
