import React from 'react';
import { useParams } from 'react-router-dom';
import { Button } from 'react-bootstrap';
import { useAuth } from '../context/AuthContext';
import PostCard from '../components/PostCard';

const dummyAuthorPosts = [
  { id: '1', title: 'First Post', tags: ['react'], author: 'Alice', preview: 'Intro to React...' },
  { id: '3', title: 'Another Post', tags: ['hooks'], author: 'Alice', preview: 'Using hooks in React...' }
];

function AuthorProfilePage() {
  const { authorId } = useParams();
  const { user } = useAuth();

  const isOwnProfile = user?.name === authorId;

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h3>{authorId}'s Posts ({dummyAuthorPosts.length})</h3>
        {!isOwnProfile && (
          <Button variant="primary" size="sm">
            Subscribe
          </Button>
        )}
      </div>

      {dummyAuthorPosts.map((post) => (
        <PostCard key={post.id} post={post} />
      ))}
    </div>
  );
}

export default AuthorProfilePage;
